package com.netflix.titus.common.model.validator;

/**
 * Validation errors are generated by {@link EntityValidator}s.  They indicate various reasons why a Job may be malformed.
 */
public class ValidationError {
    private final String field;
    private final String description;

    public ValidationError(String field, String description) {
        this.field = field;
        this.description = description;
    }

    public String getField() {
        return field;
    }

    public String getDescription() {
        return description;
    }

    public String getMessage() {
        return String.format("field: '%s', description: '%s'", field, description);
    }

    @Override
    public String toString() {
        return String.format("Validation failed: '%s'", getMessage());
    }
}
